import { AspectRatio, Box, Stack, Spacer, Center, Flex, Button, Grid, Image, Text, GridItem, Menu, MenuButton, MenuList, MenuItem} from "@chakra-ui/react"

function DashboardRocket({singleRocket}) {

    const imgBoxSize = `${95 / singleRocket.flickr_images.length}%`
    console.log(singleRocket.last_launch.webcast.replace(/watch/g, "embed/").replace(/[?v=]/g))
    const oneImage = singleRocket.flickr_images.map(img => {
        return(
            <Image
                key={img}
                borderRadius="full"
                boxSize={imgBoxSize}
                src={img}
                alt={singleRocket.name}
            />
        )
    })

    return(
        <Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.900, blue.200)"  opacity="99%">
                {singleRocket.name}
            </Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.200, blue.900)" opacity="99%">
                <Stack direction="row">
                    {oneImage}
                </Stack>
            </Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.900, blue.200)" opacity="99%">
                <AspectRatio maxW="500px" ratio={1}>
                    <iframe
                        title="Last Launch"
                        src="https://www.youtube.com/embed/QhBnZ6NPOY0"
                        allowFullScreen
                    />
                </AspectRatio>
            </Box>
        </Box>
    )
}

export default DashboardRocket
