import {RadioGroup, HStack, Radio, Input, FormControl, FormLabel, FormHelperText, AspectRatio, Box, Text, Grid, GridItem} from "@chakra-ui/react"
import {useState, useEffect} from "react"


function DashboardLaunch({singleLaunch, allComments, handelForm}) {

    // const [allComments, setAllComments] = useState([])

    const lastLaunchVideo = () => {
        if (singleLaunch.webcast === null) {
            return "https://www.youtube.com/embed/05DqIGS_koU"
        }else{
            return singleLaunch.webcast.replace(/watch/g, "embed/").replace(/[?v=]/g,"").replace(/youtu.be/g,"youtube.com/embed") 
        }
    }

    const details = () => {
        return ( 
            <Grid templateColumns="repeat(5, 1fr)" templateRows="repeat(2, 1fr)" gap={6}>
                <GridItem colStart={2} colSpan={2} rowStart={1}>
                    <Text fontFamily="'Dancing Script', cursive" fontWeight="200" fontSize="md" >
                        The {singleLaunch.name} Launch on of the {singleLaunch.rocket.name} happened on {singleLaunch.launch_date_time.substring(0,10)}
                    </Text>
                </GridItem>
                <GridItem colStart={2} colSpan={4} rowStart={2}>
                    <Text fontFamily="'Dancing Script', cursive" fontWeight="200" fontSize="md" >
                        Details: {singleLaunch.details}
                    </Text>
                </GridItem>
            </Grid>
        )
    }

    console.log(singleLaunch.id)
    console.log(singleLaunch.launch_reviews.length)

    const comments = () =>{
        if (singleLaunch.launch_reviews.length > 0){
            return (
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.900, blue.200)" padding="2">
                {mappedComments}
            </Box>
            )
        }else{
            return null
        }
    }

    const mappedComments = singleLaunch.launch_reviews.map(review =>{
            return (
              <Center py={6}>
                <Box
                  maxW={'320px'}
                  w={'full'}
                  bg={useColorModeValue('white', 'gray.900')}
                  boxShadow={'2xl'}
                  rounded={'lg'}
                  p={6}
                  textAlign={'center'}>
                  <Avatar
                    size={'xl'}
                    src={
                      'https://images.unsplash.com/photo-1520810627419-35e362c5dc07?ixlib=rb-1.2.1&q=80&fm=jpg&crop=faces&fit=crop&h=200&w=200&ixid=eyJhcHBfaWQiOjE3Nzg0fQ'
                    }
                    alt={'Avatar Alt'}
                    mb={4}
                    pos={'relative'}
                    _after={{
                      content: '""',
                      w: 4,
                      h: 4,
                      bg: 'green.300',
                      border: '2px solid white',
                      rounded: 'full',
                      pos: 'absolute',
                      bottom: 0,
                      right: 3,
                    }}
                  />
                  <Heading fontSize={'2xl'} fontFamily={'body'}>
                    Lindsey James
                  </Heading>
                  <Text fontWeight={600} color={'gray.500'} mb={4}>
                    @lindsey_jam3s
                  </Text>
                  <Text
                    textAlign={'center'}
                    color={useColorModeValue('gray.700', 'gray.400')}
                    px={3}>
                    Actress, musician, songwriter and artist. PM for work inquires or{' '}
                    <Link href={'#'} color={'blue.400'}>
                      #tag
                    </Link>{' '}
                    me in your posts
                  </Text>

                  <Stack align={'center'} justify={'center'} direction={'row'} mt={6}>
                    <Badge
                      px={2}
                      py={1}
                      bg={useColorModeValue('gray.50', 'gray.800')}
                      fontWeight={'400'}>
                      #art
                    </Badge>
                    <Badge
                      px={2}
                      py={1}
                      bg={useColorModeValue('gray.50', 'gray.800')}
                      fontWeight={'400'}>
                      #photography
                    </Badge>
                    <Badge
                      px={2}
                      py={1}
                      bg={useColorModeValue('gray.50', 'gray.800')}
                      fontWeight={'400'}>
                      #music
                    </Badge>
                  </Stack>

                  <Stack mt={8} direction={'row'} spacing={4}>
                    <Button
                      flex={1}
                      fontSize={'sm'}
                      rounded={'full'}
                      _focus={{
                        bg: 'gray.200',
                      }}>
                      Message
                    </Button>
                    <Button
                      flex={1}
                      fontSize={'sm'}
                      rounded={'full'}
                      bg={'blue.400'}
                      color={'white'}
                      boxShadow={
                        '0px 1px 25px -5px rgb(66 153 225 / 48%), 0 10px 10px -5px rgb(66 153 225 / 43%)'
                      }
                      _hover={{
                        bg: 'blue.500',
                      }}
                      _focus={{
                        bg: 'blue.500',
                      }}>
                      Follow
                    </Button>
                  </Stack>
                </Box>
              </Center>
            )
    }) 

    return(
        <Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.900, blue.200)" padding="2">
                {details()}
            </Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.200, blue.900)" >
                <AspectRatio maxW="500px" ratio={1}>
                    <iframe
                        title="Last Launch"
                        src={lastLaunchVideo()}
                        allowFullScreen
                    />
                </AspectRatio>
            </Box>
            <Box w="100%" h="500px" bgGradient="linear(to-t, blue.900, blue.200)" >
                <form onSubmit={handelForm}>
                    <FormControl id="present" isRequired>
                        <FormLabel>Were you present at during the launch</FormLabel>
                        <RadioGroup>
                            <HStack spacing="24px">
                                <Radio value="true">Yes</Radio>
                                <Radio value="false">No</Radio>
                            </HStack>
                        </RadioGroup>
                    </FormControl>
                    <FormControl id="reviewer">
                        <FormLabel>Name</FormLabel>
                        <Input type="text" />
                    </FormControl>
                    <FormControl id="comment" isRequired>
                        <FormLabel>Comment</FormLabel>
                        <Input type="text" />
                        <FormHelperText>Please Leave a Comment on the Launch</FormHelperText>
                    </FormControl>
                    <Input type="submit"/>
                </form>
            </Box>

        </Box>
    )
}

export default DashboardLaunch
